import { useRef, useState } from 'react'
import {
  Pressable,
  SafeAreaView,
  StyleSheet,
  TouchableOpacity,
  View,
  Platform,
  KeyboardAvoidingView,
  ScrollView,
  Keyboard,
} from 'react-native'
import { useNavigation } from '@react-navigation/native'
// import Icon from 'react-native-vector-icons/FontAwesome5'
import { FontAwesome5 } from '@expo/vector-icons'

import { actions, RichEditor, RichToolbar } from 'react-native-pell-rich-editor'
import {
  ArrowUturnLeftIcon,
  ArrowUturnRightIcon,
  LinkIcon,
  ListBulletIcon,
  PhotoIcon,
  EyeIcon,
} from 'react-native-heroicons/outline'
import {
  Input,
  Image,
  TextArea,
  Button,
  Text,
  Icon,
  useToast,
  Box,
  IconButton,
  keyboardDismissHandlerManager,
} from 'native-base'
import * as ImagePicker from 'expo-image-picker'
import { newPostVar } from '../../variables/newPost'
import { flexbox } from 'native-base/lib/typescript/theme/styled-system'
import { useReactiveVar } from '@apollo/client'
import ScreenHeader from '@components/ScreenHeader/ScreenHeader'

interface AddPostScreenProps {}

const AddPostScreen: React.FunctionComponent<AddPostScreenProps> = ({}) => {
  const richText = useRef()
  const navigation = useNavigation()
  const existingNewPostData = useReactiveVar(newPostVar)
  const [formProgress, setFormProgress] = useState(1)
  const [descHTML, setDescHTML] = useState('')
  const [title, setTitle] = useState(existingNewPostData.title)
  const [intro, setIntro] = useState(existingNewPostData.intro)
  const [isButtonVisible, setIsButtonVisible] = useState(true)

  const [showDescError, setShowDescError] = useState(false)
  const [image, setImage] = useState('')
  const toast = useToast()
  const id = 'error'
  const scrollViewRef = useRef(null)

  const pickImage = async () => {
    // No permissions request is necessary for launching the image library
    let result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.All,
      allowsEditing: true,
      aspect: [4, 3],
      quality: 1,
    })

    if (result.cancelled) {
    }
    if (!result.cancelled) {
      setIsButtonVisible(false)
      setImage(result.uri)
    }
  }
  const richTextHandle = (descriptionText) => {
    if (descriptionText) {
      setShowDescError(false)
      setDescHTML(descriptionText)
    } else {
      setDescHTML('')
    }
  }

  const submitContentHandle = () => {
    const replaceHTML = descHTML.replace(/<(.|\n)*?>/g, '').trim()
    const replaceWhiteSpace = replaceHTML.replace(/&nbsp;/g, '').trim()
    if (
      replaceWhiteSpace.length <= 0 ||
      !title ||
      title.length === 0 ||
      (!intro && intro.length === 0) ||
      !image ||
      image.length === 0
    ) {
      if (!toast.isActive(id)) {
        toast.show({
          id,
          title: 'ðŸ¤” Veuillez remplir tous les champs',
        })
      }
    } else {
      newPostVar({
        title: title,
        image: image,
        content: '',
        intro: intro,
      })
      setFormProgress(2)
    }
  }

  if (formProgress === 1) {
    return (
      <SafeAreaView
        className={` bg-white ${Platform.OS === 'ios' ? 'pb-0 -mt-2' : 'pb-1 -mt-1'}  `}
      >
        <ScreenHeader arrowDirection='no-arrow' />
        <Text>{formProgress}</Text>

        <ScrollView
          ref={scrollViewRef}
          onContentSizeChange={() => {
            scrollViewRef.current?.scrollToEnd()
          }}
          showsVerticalScrollIndicator={false}
          className={`bg-white mx-3 ${Platform.OS === 'ios' ? 'pb-0 ' : 'pb-1 mt-8'}`}
        >
          {/* <Text className='text-xl color-deepBlue font-ralewayBold  mt-4 ml-3 mb-2 text-center'>
            Proposer une publication
          </Text> */}
          <Input
            size='xl'
            maxLength={40}
            placeholder='Titre'
            placeholderTextColor='#272E67'
            onChangeText={(val) => setTitle(val)}
            value={title}
          />
          <View className='mb-2 mt-2'>
            <TextArea
              h={20}
              size='xl'
              maxLength={150}
              placeholder='Introduction'
              placeholderTextColor='#272E67'
              w='100%'
              // fontSize='md'
              onChangeText={(val) => setIntro(val)}
              value={intro}
            />
          </View>
          {isButtonVisible && (
            <Button
              variant='outline'
              onPress={pickImage}
              className='flex flex-row items-center justify-center'
            >
              <View>
                <Text className='color-grey font-bold '>
                  <PhotoIcon size='24' color='grey' />
                  SÃ©lectionnez l'image principale
                </Text>
              </View>
            </Button>
          )}
          {image && image !== '' && (
            <>
              <TouchableOpacity onPress={pickImage}>
                <Image
                  className='self-center m-2 rounded-md'
                  width='200'
                  alt='uploaded image'
                  height='100'
                  resizeMode='cover'
                  source={{
                    uri: image,
                  }}
                />
              </TouchableOpacity>
              <Button
                onPress={() => {
                  Keyboard.dismiss()
                  submitContentHandle()
                }}
                className='mx-20 my-4'
              >
                <Text className='color-white'>
                  <EyeIcon size='40' color='white' />
                </Text>
              </Button>
            </>
          )}
          {/* {showDescError && (
            <Text style={styles.errorTextStyle}>Veuillez remplir tous les champs ðŸ¤”</Text>
          )} */}
        </ScrollView>
      </SafeAreaView>
    )
  } else if (formProgress == 2) {
    return (
      <SafeAreaView
        className={` bg-white ${Platform.OS === 'ios' ? 'pb-0 -mt-2' : 'pb-1 -mt-1'}  `}
      >
        <ScreenHeader arrowDirection='no-arrow' />

        <ScrollView
          ref={scrollViewRef}
          onContentSizeChange={() => {
            scrollViewRef.current?.scrollToEnd()
          }}
          showsVerticalScrollIndicator={false}
          className={`bg-white mx-3 ${Platform.OS === 'ios' ? 'pb-0 ' : 'pb-1 mt-8'}`}
        >
          {/* <Text className='text-xl color-deepBlue font-ralewayBold  mt-4 ml-3 mb-2 text-center'>
            Proposer une publication
          </Text> */}

          {isButtonVisible && (
            <Button
              variant='outline'
              onPress={pickImage}
              className='flex flex-row items-center justify-center'
            >
              <View>
                <Text className='color-grey font-bold '>
                  <PhotoIcon size='24' color='grey' />
                  SÃ©lectionnez l'image principale
                </Text>
              </View>
            </Button>
          )}
          {image && image !== '' && (
            <>
              <TouchableOpacity onPress={pickImage}>
                <Image
                  className='self-center m-2 rounded-md'
                  width='200'
                  alt='uploaded image'
                  height='100'
                  resizeMode='cover'
                  source={{
                    uri: image,
                  }}
                />
              </TouchableOpacity>
              <Button
                onPress={() => {
                  Keyboard.dismiss()
                  submitContentHandle()
                }}
                className='mx-20 my-4'
              >
